import { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import instance from '../api';
import Breadcrumbs from './Breadcrumbs';

export default function Layout() {
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  // 登入檢查
  useEffect(() => {
    const token = localStorage.getItem('access_token');

    if (!token) {
      navigate('/login');
      return;
    }

    const fetchUser = async () => {
      try {
        // 可以抓自己的 user info，或用 token 驗證是否有效
        const res = await instance.get('users/me/');
        setUser(res.data);
      } catch (err) {
        console.error('驗證失敗', err);
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // 自動生成 Breadcrumb
  const generateBreadcrumb = () => {
    const paths = location.pathname
      .split('/')
      .filter(Boolean)
      .map((segment, idx, arr) => {
        const link = '/' + arr.slice(0, idx + 1).join('/');
        return { name: segment.charAt(0).toUpperCase() + segment.slice(1), link };
      });

    return [{ name: '首頁', link: '/' }, ...paths];
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <Breadcrumbs paths={generateBreadcrumb()} />
      <div>{user ? `Hi, ${user.name}` : ''}</div>
    </div>
  );
}
