import React, { useEffect, useState } from "react";
import AssetForm from "../components/AssetForm";
import PeopleSelectModal from "../components/PeopleSelectModal";
import api from "../api";

const Inventory = () => {
  const [assets, setAssets] = useState([]);
  const [editingAsset, setEditingAsset] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [detailedMode, setDetailedMode] = useState(
    localStorage.getItem("inventoryDetailedMode") === "true"
  );
  const [showPeopleModal, setShowPeopleModal] = useState(false);
  const [selectedOwner, setSelectedOwner] = useState(null);

  const fields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "asset_tag", label: "財產編號", type: "text" },
    { name: "type", label: "種類", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "owner_name", label: "持有人", type: "text" },
  ];

  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await api.get("inventory/assets/");
        setAssets(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAssets();
  }, []);

  // 單筆新增或更新
  const handleAddOrUpdate = async (asset) => {
    try {
      const payload = {
        asset_tag: asset.asset_tag,
        name: asset.name,
        type: asset.type,
        price: asset.price,
        owner_user: selectedOwner?.id ?? null,
      };

      let res;
      if (editingAsset) {
        res = await api.put(`inventory/assets/${editingAsset.id}/`, payload);
        setAssets((prev) =>
          prev.map((a) => (a.id === editingAsset.id ? res.data : a))
        );
      } else {
        res = await api.post("inventory/assets/", payload);
        setAssets((prev) => [...prev, res.data]);
      }

      setEditingAsset(null);
      setSelectedOwner(null);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  // CSV 匯入
  const handleAddCSV = async (file) => {
    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await api.post("inventory/assets/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setAssets((prev) => [...prev, ...res.data]);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("CSV 匯入失敗，請查看 console");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`inventory/assets/${id}/`);
      setAssets((prev) => prev.filter((p) => p.id !== id));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  const handleEdit = (asset) => {
    setEditingAsset(asset);
    if (asset.holder) setSelectedOwner(asset.holder);
  };
  const cancelEdit = () => {
    setEditingAsset(null);
    setSelectedOwner(null);
  };

  const toggleDetailedMode = () => {
    setDetailedMode((prev) => {
      localStorage.setItem("inventoryDetailedMode", !prev);
      return !prev;
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <button onClick={toggleDetailedMode} style={{ marginBottom: "10px" }}>
        {detailedMode ? "切換為簡略模式" : "切換為詳細模式"}
      </button>

      <AssetForm
        fields={fields}
        asset={editingAsset}
        selectedOwner={selectedOwner}
        onOwnerSelect={setSelectedOwner}
        onAddOrUpdate={handleAddOrUpdate}
        onAddCSV={handleAddCSV}
      />

      {editingAsset && (
        <button
          onClick={cancelEdit}
          style={{ marginBottom: "10px", backgroundColor: "#ccc" }}
        >
          取消編輯
        </button>
      )}

      <h2>資產列表</h2>
      {assets.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>名稱</th>
              <th>財產編號</th>
              <th>種類</th>
              <th>價格</th>
              <th>持有人</th>
              {detailedMode && <th>借用時間</th>}
              {detailedMode && <th>歸還時間</th>}
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {assets.map((p, idx) => (
              <tr key={p.id}>
                <td>{idx + 1}</td>
                <td>{p.product.name}</td>
                <td>{p.asset_tag}</td>
                <td>{p.product.type}</td>
                <td>{p.product.price}</td>
                <td>{p.holder?.name ?? "無"}</td>
                {detailedMode && <td>{p.borrowed_at ?? "-"}</td>}
                {detailedMode && <td>{p.returned_at ?? "-"}</td>}
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {showPeopleModal && (
        <PeopleSelectModal
          onClose={() => setShowPeopleModal(false)}
          onSelect={setSelectedOwner}
        />
      )}
    </div>
  );
};

export default Inventory;
